function maxnum(str){
    var json = {};
    for(var i=0;i<str.length;i++){
        if(!json[str.charAt(i)]){
            json[str.charAt(i)] = 1;
        }else{
            json[str.charAt(i)]++;
        }
        console.log(json);
    }
    var max = 0;
    var Index = '';
    for(var i in json){
        if(json[i]>max){
            max = json[i];
            Index = i;
        }
    }
    console.log(max + Index);   
}


