### validate.js



```
reg = pattern[reg] ? pattern[reg] : reg
```



```js
/**
 * 
 */
const pattern = {
  name: /^([\w-]+|[\u4e00-\u9fa5]+)$/, // 
  passWord: /^\w+$/ // 
}

/**
 * 
 * @param {String} reg - {pattern} or 
 * @param {String} msg1 - 
 * @param {String} msg2 - 
 */
const validate = (reg, msg1, msg2) => {
  return (rule, value, callback) => {
    if (!value) {
      if (!msg2) {
        callback()
      }
      callback(new Error(msg2))
    }
    reg = pattern[reg] ? pattern[reg] : reg
    if (!reg.test(value)) {
      callback(new Error(msg1))
    } else {
      callback()
    }
  }
}

export default validate

```



